      'linear-gradient(45deg, #FF4B3A 0%, #FF8F6B 100%)',
      'linear-gradient(45deg, #FF6B5C 0%, #FF9D7D 100%)',
      'linear-gradient(45deg, #FF4B3A 0%, #FF8F6B 100%)'
    ],
    transition: {
      duration: 5,
      repeat: Infinity,
      repeatType: "reverse"
    }
  }
};

const FloatingFoodIcons = () => (
  <Box
    sx={{
      position: 'absolute',
      width: '100%',
      height: '100%',
      overflow: 'hidden',
      pointerEvents: 'none',
      zIndex: 0
    }}
