    {[FastfoodIcon, LocalDiningIcon, RamenDiningIcon].map((Icon, index) => (
      <Box
        key={index}
        component={Icon}
        sx={{
          position: 'absolute',
          fontSize: { xs: '3rem', md: '4rem' },
          color: 'primary.main',
          opacity: 0.2,
          animation: `${float} ${3 + index}s ease-in-out infinite`,
          left: `${20 + index * 30}%`,
          top: `${10 + index * 20}%`,
          transform: `rotate(${index * 45}deg)`,
        }}
      />
    ))}
  </Box>
);

const BackgroundSVGs = () => (
  <>
    <Box
      sx={{
        position: 'absolute',
        width: '100%',
        height: '100%',
        opacity: 0.03,
        zIndex: 0,
        overflow: 'hidden',
        pointerEvents: 'none',
      }}
    >
      {/* Plate Pattern */}
      <svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg">
        <pattern id="plate-pattern" x="0" y="0" width="40" height="40" patternUnits="userSpaceOnUse">
          <circle cx="20" cy="20" r="15" fill="none" stroke="currentColor" strokeWidth="1"/>
          <circle cx="20" cy="20" r="8" fill="none" stroke="currentColor" strokeWidth="1"/>
        </pattern>
        <rect x="0" y="0" width="100%" height="100%" fill="url(#plate-pattern)"/>
      </svg>
    </Box>
    <Box
      sx={{
        position: 'absolute',
        width: '100%',
        height: '100%',
        opacity: 0.02,
        zIndex: 0,
        overflow: 'hidden',
        pointerEvents: 'none',
        transform: 'rotate(30deg)',
      }}
