            Kerala, India<br />
            Phone: +91 123-456-7890<br />
            Email: info@thalasseryrestaurant.com
          </Typography>
        </Grid>
        <Grid 
          item 
          xs={12} 
          sm={4}
          component={motion.div}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.4 }}
        >
          <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              color: 'white',
              fontWeight: 600,
              position: 'relative',
              '&::after': {
                content: '""',
                position: 'absolute',
                bottom: -8,
                left: 0,
                width: '40px',
                height: '2px',
                bgcolor: 'rgba(255,255,255,0.5)'
              }
            }}
          >
            Hours
          </Typography>
          <Typography sx={{ color: 'rgba(255,255,255,0.9)' }}>
            Monday - Sunday<br />
            11:00 AM - 11:00 PM<br />
            Delivery Available
          </Typography>
        </Grid>
      </Grid>
      <Typography 
        component={motion.p}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5 }}
        sx={{ 
          mt: 4,
          textAlign: 'center',
          color: 'rgba(255,255,255,0.7)',
          borderTop: '1px solid rgba(255,255,255,0.1)',
          pt: 3
        }}
      >
        {new Date().getFullYear()} Thalassery Restaurant. All rights reserved.
      </Typography>
    </Container>
  </Box>
);

const FoodCardSkeleton = () => (
  <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
    <Skeleton variant="rectangular" height={160} animation="wave" />
    <CardContent sx={{ flexGrow: 1, p: 2 }}>
      <Skeleton variant="text" width="80%" height={32} animation="wave" />
      <Skeleton variant="text" width="60%" height={24} animation="wave" />
      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Skeleton variant="text" width="30%" height={32} animation="wave" />
        <Skeleton variant="rectangular" width="80px" height={36} animation="wave" />
      </Box>
    </CardContent>
  </Card>
);

const HomePage = () => {
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(true);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const menuRef = useRef(null);

  // Simulate loading
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1500);
    return () => clearTimeout(timer);
  }, []);

  const handleFilterChange = (event, newValue) => {
    setLoading(true);
    setFilter(newValue);
    setTimeout(() => setLoading(false), 500);
  };

  const filteredItems = foodItems.filter(item => 
    filter === 'all' ? true : item.type === filter
  );

  return (
    <Box sx={{ 
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      bgcolor: '#FF4B3A',
    }}>
      <BackgroundSVGs />

      {/* Header */}

        <Toolbar>
          <RestaurantMenuIcon sx={{ mr: 2, color: 'white', fontSize: '2rem' }} />
          <Typography 
            variant="h6" 
            component={motion.div}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            sx={{ 
              flexGrow: 1, 
              color: 'white', 
              fontWeight: 600,
              fontSize: { xs: '1.2rem', md: '1.5rem' }
            }}
          >
            Thalassery Restaurant
          </Typography>
          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <IconButton color="inherit" aria-label="cart">
              <ShoppingCartIcon />
            </IconButton>
          </motion.div>
        </Toolbar>

      <Toolbar /> {/* Spacer for fixed AppBar */}

      {/* Main Content */}
      <Container 
        maxWidth="xl" 
        sx={{ 
          flex: 1,
          py: 4,
          display: 'flex',
          flexDirection: 'column',
          bgcolor: 'white',
          borderRadius: 4,
          boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
          position: 'relative',
          zIndex: 1,
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: '2px',
            background: 'linear-gradient(90deg, transparent, rgba(0,0,0,0.1), transparent)'
