        }}
      >
        {/* Hero Section */}
        <Box
          component={motion.div}
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          sx={{ 
            textAlign: 'center', 
            mb: 4,
            position: 'relative',
            overflow: 'hidden',
            py: { xs: 4, md: 8 },
            px: 2,
            borderRadius: 4,
            background: (theme) => `linear-gradient(135deg, ${theme.palette.primary.main}11 0%, ${theme.palette.primary.light}22 100%)`,
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
