          <FloatingFoodIcons />
          <BackgroundSVGs />

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <Typography 
              variant="h2" 
              component="h1" 
              gutterBottom
              sx={{
                fontWeight: 700,
                fontSize: { xs: '2rem', sm: '3rem', md: '4rem' },
                color: 'primary.main',
                textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
                position: 'relative',
                zIndex: 1
              }}
            >
              Thalassery Restaurant
            </Typography>
            <Typography 
              variant="h5" 
              sx={{ 
                mb: 4, 
                opacity: 0.9,
                color: 'text.primary',
                fontSize: { xs: '1rem', sm: '1.25rem', md: '1.5rem' },
                position: 'relative',
                zIndex: 1
              }}
            >
              Experience the taste of tradition
            </Typography>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4 }}
            >
              <Button
                variant="contained"
                color="primary"
                size="large"
                startIcon={<RestaurantMenuIcon />}
                onClick={() => menuRef.current?.scrollIntoView({ behavior: 'smooth' })}
                sx={{ 
                  borderRadius: '50px',
                  px: 4,
                  py: 1.5,
                  fontSize: '1.1rem',
                  boxShadow: '0 4px 16px rgba(0,0,0,0.1)',
                  '&:hover': {
                    transform: 'translateY(-2px)',
                    boxShadow: '0 6px 20px rgba(0,0,0,0.15)',
                  }
                }}
              >
                Explore Menu
              </Button>
            </motion.div>
          </motion.div>
        </Box>

        {/* Menu Section */}
        <Box 
          ref={menuRef}
          sx={{ 
            position: 'relative',
            minHeight: '600px',
            mt: 4,
            scrollMarginTop: '80px'
          }}
        >
          {/* Filter Tabs */}
          <Box sx={{ 
            mb: 3,
            bgcolor: 'background.paper',
            borderRadius: 2,
            boxShadow: '0 2px 12px rgba(0,0,0,0.08)',
          }}>
            <Tabs 
              value={filter} 
              onChange={handleFilterChange}
              variant="scrollable"
              scrollButtons="auto"
              allowScrollButtonsMobile
              sx={{
                '& .MuiTab-root': {
                  fontSize: '0.95rem',
                  fontWeight: 500,
                  py: 1.5,
                  minWidth: 'auto',
                  minHeight: '48px',
                  textTransform: 'none',
                },
                '& .MuiTab-root .MuiSvgIcon-root': {
                  mr: 1,
                  fontSize: '1.2rem'
                }
              }}
            >
              <Tab 
                icon={<RestaurantMenuIcon />} 
                iconPosition="start" 
                label="All Menu" 
                value="all" 
              />
              <Tab 
                icon={<GrassIcon />} 
                iconPosition="start" 
                label="Vegetarian" 
                value="veg" 
              />
              <Tab 
                icon={<RamenDiningIcon />} 
                iconPosition="start" 
                label="Non-Vegetarian" 
                value="non-veg" 
              />
              <Tab 
                icon={<SetMealIcon />} 
                iconPosition="start" 
                label="Chinese" 
                value="chinese" 
              />
              <Tab 
                icon={<LunchDiningIcon />} 
                iconPosition="start" 
                label="Snacks" 
                value="snacks" 
              />
              <Tab 
                icon={<CakeIcon />} 
                iconPosition="start" 
                label="Desserts" 
                value="dessert" 
              />
              <Tab 
                icon={<IcecreamIcon />} 
                iconPosition="start" 
                label="Sweets" 
                value="sweets" 
              />
            </Tabs>
          </Box>

          {/* Menu Items and Beverages */}
          <Box sx={{ position: 'relative' }}>
            <Grid container spacing={3}>
              {/* Menu Items */}
              <Grid item xs={12} md={9}>
                <Grid container spacing={2}>
                  {loading ? (
                    Array.from(new Array(8)).map((_, index) => (
                      <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                        <FoodCardSkeleton />
                      </Grid>
                    ))
                  ) : (
                    filteredItems.map(item => (
                      <Grid item xs={12} sm={6} md={4} lg={3} key={item.id}>
                        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                          <CardMedia
                            component="img"
                            height="160"
                            image={item.image}
                            alt={item.name}
                            sx={{ objectFit: 'cover' }}
                          />
                          <CardContent sx={{ flexGrow: 1, p: 2 }}>
                            <Typography variant="h6" gutterBottom noWrap>
                              {item.name}
                            </Typography>
                            <Tooltip title={item.description}>
                              <Typography 
                                variant="body2" 
                                color="text.secondary" 
                                sx={{
                                  overflow: 'hidden',
                                  textOverflow: 'ellipsis',
                                  whiteSpace: 'nowrap',
                                  mb: 2
                                }}
                              >
                                {item.description}
                              </Typography>
                            </Tooltip>
                            <Box sx={{ 
                              display: 'flex', 
                              justifyContent: 'space-between',
                              alignItems: 'center'
                            }}>
                              <Typography variant="h6" color="primary">
                                {item.price}
                              </Typography>
                              <Button 
                                size="small"
                                variant="contained" 
                                color="primary"
                                startIcon={<ShoppingCartIcon />}
                              >
                                Add
                              </Button>
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))
                  )}
                </Grid>
              </Grid>

              {/* Beverages Section */}
              <Grid item xs={12} md={3}>
                <Box sx={{ 
                  position: 'sticky',
                  top: '100px',
                  pl: 2
                }}>
                  <Box sx={{ 
                    backgroundColor: '#fff',
                    borderRadius: 2,
                    p: 3,
                  }}>
                    <Typography 
                      variant="h5" 
                      gutterBottom 
                      sx={{ 
                        mb: 3, 
                        fontWeight: 600,
                        borderBottom: '2px solid',
                        borderColor: 'primary.main',
                        pb: 1
                      }}
                    >
                      Beverages & Drinks
                    </Typography>
                    <Stack spacing={2}>
                      {beverages.map(beverage => (
                        <Card 
                          key={beverage.id} 
                          elevation={0}
                          sx={{ 
                            display: 'flex',
                            height: 90,
                            border: '1px solid',
                            borderColor: 'grey.200',
                            transition: 'all 0.3s ease',
                            '&:hover': {
                              borderColor: 'primary.main',
                              transform: 'translateY(-2px)',
                              boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
                            }
                          }}
                        >
                          <CardMedia
                            component="img"
                            sx={{ 
                              width: 90,
                              height: '100%',
                              objectFit: 'cover'
                            }}
                            image={beverage.image}
                            alt={beverage.name}
                          />
                          <CardContent 
                            sx={{ 
                              flex: 1,
                              p: 2,
                              '&:last-child': { pb: 2 },
                              display: 'flex',
                              flexDirection: 'column',
                              justifyContent: 'space-between'
                            }}
                          >
                            <Typography 
                              variant="h6" 
                              sx={{ 
                                fontSize: '1rem',
                                fontWeight: 600
                              }}
                            >
                              {beverage.name}
                            </Typography>
                            <Box 
                              sx={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                mt: 1
                              }}
                            >
                              <Typography 
                                variant="h6" 
                                color="primary"
                                sx={{ 
                                  fontWeight: 600,
                                  fontSize: '1rem'
                                }}
                              >
                                {beverage.price}
                              </Typography>
                              <Button 
                                variant="contained" 
                                color="primary"
                                size="small"
                                startIcon={<ShoppingCartIcon />}
                                sx={{ 
                                  borderRadius: '20px',
                                  px: 2,
                                  textTransform: 'none',
                                  fontWeight: 500
                                }}
                              >
                                Add
                              </Button>
                            </Box>
                          </CardContent>
                        </Card>
                      ))}
                    </Stack>
                  </Box>
                </Box>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>

      <Footer />
    </Box>
  );
};

export default HomePage;
