import React, { useState } from 'react';
import { 
  Box, 
  Container, 
  Grid, 
  Typography, 
  Tabs, 
  Tab,
  Card,
  CardMedia,
  CardContent,
  Button,
  IconButton,
  AppBar,
  Toolbar,
  useTheme,
  useMediaQuery,
  Tooltip,
  Paper,
  Stack,
  Link
} from '@mui/material';
import { motion } from 'framer-motion';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import RestaurantMenuIcon from '@mui/icons-material/RestaurantMenu';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import { keyframes } from '@emotion/react';
import FastfoodIcon from '@mui/icons-material/Fastfood';
import LocalDiningIcon from '@mui/icons-material/LocalDining';
import RamenDiningIcon from '@mui/icons-material/RamenDining';

// Mock data for food items
const foodItems = [
  // Kerala Cuisine
  { 
    id: 1, 
    name: 'Kerala Biriyani', 
    type: 'non-veg', 
    region: 'kerala',
    price: 250, 
    image: 'https://images.unsplash.com/photo-1563379091339-03b21ab4a4f8?w=500&q=80',
    description: 'Aromatic rice with spices and tender meat'
  },
  { 
    id: 2, 
    name: 'Fish Curry', 
    type: 'non-veg',
    region: 'kerala', 
    price: 180, 
    image: 'https://images.unsplash.com/photo-1626777552726-4a6b54c97e46?w=500&q=80',
    description: 'Traditional Kerala style fish curry'
  },
  // North Indian
  { 
    id: 3, 
    name: 'Butter Chicken', 
    type: 'non-veg',
    region: 'north', 
    price: 280, 
    image: 'https://images.unsplash.com/photo-1603894584373-5ac82b2ae398?w=500&q=80',
    description: 'Creamy tomato-based curry with tender chicken'
  },
  { 
    id: 4, 
    name: 'Dal Makhani', 
    type: 'veg',
    region: 'north', 
    price: 160, 
    image: 'https://images.unsplash.com/photo-1546833999-b9f581a1996d?w=500&q=80',
    description: 'Creamy black lentils cooked overnight'
  },
  // Gujarati
  { 
    id: 5, 
    name: 'Dhokla', 
    type: 'veg',
    region: 'gujarat', 
    price: 120, 
    image: 'https://images.unsplash.com/photo-1589301760014-d929f3979dbc?w=500&q=80',
    description: 'Steamed fermented rice and chickpea cake'
  },
  { 
    id: 6, 
    name: 'Undhiyu', 
    type: 'veg',
    region: 'gujarat', 
    price: 220, 
    image: 'https://images.pexels.com/photos/9797029/pexels-photo-9797029.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Mixed vegetable curry, Gujarati specialty'
  },
  // Rajasthani
  { 
    id: 7, 
    name: 'Laal Maas', 
    type: 'non-veg',
    region: 'rajasthan', 
    price: 320, 
    image: 'https://images.pexels.com/photos/2474661/pexels-photo-2474661.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Spicy mutton curry with red chilies'
  },
  { 
    id: 8, 
    name: 'Dal Baati', 
    type: 'veg',
    region: 'rajasthan', 
    price: 180, 
    image: 'https://images.pexels.com/photos/4449068/pexels-photo-4449068.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Baked wheat balls served with lentils'
  },
  // Bihari
  { 
    id: 9, 
    name: 'Litti Chokha', 
    type: 'veg',
    region: 'bihar', 
    price: 150, 
    image: 'https://images.pexels.com/photos/7625056/pexels-photo-7625056.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Wheat balls with spiced mashed potatoes'
  },
  { 
    id: 10, 
    name: 'Bihari Kebab', 
    type: 'non-veg',
    region: 'bihar', 
    price: 280, 
    image: 'https://images.pexels.com/photos/2233729/pexels-photo-2233729.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Spiced meat patties, Bihar style'
  },
  // Himachal
  { 
    id: 11, 
    name: 'Dham', 
    type: 'veg',
    region: 'himachal', 
    price: 250, 
    image: 'https://images.pexels.com/photos/958545/pexels-photo-958545.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Traditional Himachali festive meal'
  },
  { 
    id: 12, 
    name: 'Chha Gosht', 
    type: 'non-veg',
    region: 'himachal', 
    price: 300, 
    image: 'https://images.pexels.com/photos/7625318/pexels-photo-7625318.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Slow-cooked lamb in yogurt gravy'
  }
];

// Update the beverages data with better images
const beverages = [
  { 
    id: 1, 
    name: 'Kerala Coffee', 
    price: 40, 
    image: 'https://images.pexels.com/photos/312418/pexels-photo-312418.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Traditional filter coffee'
  },
  { 
    id: 2, 
    name: 'Masala Chai', 
    price: 30, 
    image: 'https://images.pexels.com/photos/5946975/pexels-photo-5946975.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Spiced Indian tea'
  },
  { 
    id: 3, 
    name: 'Mango Lassi', 
    price: 50, 
    image: 'https://images.pexels.com/photos/4551832/pexels-photo-4551832.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Sweet yogurt-based drink'
  },
  { 
    id: 4, 
    name: 'Thandai', 
    price: 60, 
    image: 'https://images.pexels.com/photos/6413705/pexels-photo-6413705.jpeg?auto=compress&cs=tinysrgb&w=400',
    description: 'Spiced milk drink with nuts'
  }
];

const float = keyframes`
  0% { transform: translateY(0px) }
  50% { transform: translateY(-20px) }
  100% { transform: translateY(0px) }
`;

const rotate = keyframes`
  from { transform: rotate(0deg) }
  to { transform: rotate(360deg) }
`;

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px)
  }
  to {
    opacity: 1;
    transform: translateY(0)
  }
`;

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { 
    opacity: 1,
    transition: { 
      duration: 0.5,
      when: "beforeChildren",
      staggerChildren: 0.05
    }
  }
};

const itemVariants = {
  hidden: { y: 20, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      type: "spring",
      stiffness: 100
    }
  }
};

const backgroundVariants = {
  animate: {
    background: [
      'linear-gradient(45deg, #FF4B3A 0%, #FF8F6B 100%)',
      'linear-gradient(45deg, #FF6B5C 0%, #FF9D7D 100%)',
      'linear-gradient(45deg, #FF4B3A 0%, #FF8F6B 100%)'
    ],
    transition: {
      duration: 5,
      repeat: Infinity,
      repeatType: "reverse"
    }
  }
};

const FloatingFoodIcons = () => (
  <Box
    sx={{
      position: 'absolute',
      width: '100%',
      height: '100%',
      overflow: 'hidden',
      pointerEvents: 'none',
      zIndex: 0
    }}
  >
    {[FastfoodIcon, LocalDiningIcon, RamenDiningIcon].map((Icon, index) => (
      <Box
        key={index}
        component={Icon}
        sx={{
          position: 'absolute',
          fontSize: { xs: '3rem', md: '4rem' },
          color: 'primary.main',
          opacity: 0.2,
          animation: `${float} ${3 + index}s ease-in-out infinite`,
          left: `${20 + index * 30}%`,
          top: `${10 + index * 20}%`,
          transform: `rotate(${index * 45}deg)`,
        }}
      />
    ))}
  </Box>
);

const Footer = () => (
  <Box
    component="footer"
    sx={{
      bgcolor: 'primary.dark',
      color: 'white',
      py: 6,
      mt: 8
    }}
  >
    <Container maxWidth="lg">
      <Grid container spacing={4}>
        <Grid item xs={12} sm={4}>
          <Typography variant="h6" gutterBottom>
            About Us
          </Typography>
          <Typography variant="body2">
            Thalassery Restaurant brings you authentic flavors from across India. 
            Our passion for traditional cuisine and commitment to quality make every meal special.
          </Typography>
        </Grid>
        <Grid item xs={12} sm={4}>
          <Typography variant="h6" gutterBottom>
            Contact Info
          </Typography>
          <Stack spacing={2}>
            <Box display="flex" alignItems="center" gap={1}>
              <LocationOnIcon />
              <Typography variant="body2">
                123 Foodie Street, Thalassery, Kerala
              </Typography>
            </Box>
            <Box display="flex" alignItems="center" gap={1}>
              <PhoneIcon />
              <Typography variant="body2">
                +91 98765 43210
              </Typography>
            </Box>
            <Box display="flex" alignItems="center" gap={1}>
              <EmailIcon />
              <Typography variant="body2">
                info@thalasseryrestaurant.com
              </Typography>
            </Box>
          </Stack>
        </Grid>
        <Grid item xs={12} sm={4}>
          <Typography variant="h6" gutterBottom>
            Follow Us
          </Typography>
          <Stack direction="row" spacing={2}>
            <IconButton color="inherit">
              <FacebookIcon />
            </IconButton>
            <IconButton color="inherit">
              <InstagramIcon />
            </IconButton>
            <IconButton color="inherit">
              <TwitterIcon />
            </IconButton>
          </Stack>
        </Grid>
      </Grid>
      <Typography variant="body2" align="center" sx={{ mt: 4, opacity: 0.7 }}>
        {new Date().getFullYear()} Thalassery Restaurant. All rights reserved.
      </Typography>
    </Container>
  </Box>
);

const HomePage = () => {
  const [filter, setFilter] = useState('all');
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleFilterChange = (event, newValue) => {
    setFilter(newValue);
  };

  const filteredItems = foodItems.filter(item => 
    filter === 'all' ? true : item.type === filter
  );

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
      <AppBar 
        position="static" 
        color="transparent" 
        elevation={0}
        component={motion.div}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100 }}
      >
        <Toolbar>
          <motion.div
            initial={{ rotate: 0 }}
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          >
            <RestaurantMenuIcon sx={{ mr: 2, color: 'primary.main', fontSize: '2rem' }} />
          </motion.div>
          <Typography 
            variant="h6" 
            component={motion.div}
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            sx={{ 
              flexGrow: 1, 
              color: 'primary.main', 
              fontWeight: 600,
              fontSize: { xs: '1.2rem', md: '1.5rem' }
            }}
          >
            Thalassery Restaurant
          </Typography>
          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <IconButton color="primary" aria-label="cart">
              <ShoppingCartIcon />
            </IconButton>
          </motion.div>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ py: 4 }}>
        <motion.div
          initial="hidden"
          animate="visible"
          variants={{
            hidden: { opacity: 0, y: 50 },
            visible: {
              opacity: 1,
              y: 0,
              transition: {
                duration: 0.8,
                staggerChildren: 0.2
              }
            }
          }}
        >
          {/* Hero Section */}
          <Box
            sx={{ 
              textAlign: 'center', 
              mb: 4,
              position: 'relative',
              overflow: 'hidden',
              py: { xs: 4, md: 8 },
              px: 2,
              borderRadius: 4,
              background: (theme) => `linear-gradient(135deg, ${theme.palette.primary.main}22 0%, ${theme.palette.primary.light}33 100%)`,
              boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            }}
          >
            <FloatingFoodIcons />

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <Typography 
                variant="h2" 
                component="h1" 
                gutterBottom
                sx={{
                  fontWeight: 700,
                  fontSize: { xs: '2rem', sm: '3rem', md: '4rem' },
                  color: 'primary.main',
                  textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
                  position: 'relative',
                  zIndex: 1
                }}
              >
                Thalassery Restaurant
              </Typography>
              <Typography 
                variant="h5" 
                sx={{ 
                  mb: 4, 
                  opacity: 0.9,
                  color: 'text.primary',
                  fontSize: { xs: '1rem', sm: '1.25rem', md: '1.5rem' },
                  position: 'relative',
                  zIndex: 1
                }}
              >
                Experience the taste of tradition
              </Typography>

              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                <Button
                  variant="contained"
                  color="primary"
                  size="large"
                  startIcon={<RestaurantMenuIcon />}
                  sx={{ 
                    borderRadius: '50px',
                    px: 4,
                    py: 1.5,
                    fontSize: '1.1rem',
                    boxShadow: '0 4px 16px rgba(0,0,0,0.1)',
                    '&:hover': {
                      transform: 'translateY(-2px)',
                      boxShadow: '0 6px 20px rgba(0,0,0,0.15)',
                    }
                  }}
                >
                  Explore Menu
                </Button>
              </motion.div>
            </motion.div>
          </Box>

          {/* Filter Tabs */}
          <Box sx={{ 
            borderRadius: 2,
            mb: 3,
            bgcolor: 'background.paper',
            boxShadow: '0 2px 12px rgba(0,0,0,0.08)'
          }}>
            <Tabs 
              value={filter} 
              onChange={handleFilterChange}
              centered
              sx={{
                '& .MuiTab-root': {
                  fontSize: '1rem',
                  fontWeight: 500,
                  py: 1.5
                }
              }}
            >
              <Tab label="All Menu" value="all" />
              <Tab label="Vegetarian" value="veg" />
              <Tab label="Non-Vegetarian" value="non-veg" />
            </Tabs>
          </Box>

          <Grid container spacing={3}>
            {/* Menu Items */}
            <Grid item xs={12} md={9}>
              <Grid container spacing={2}>
                {filteredItems.map(item => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={item.id}>
                    <motion.div variants={itemVariants}>
                      <Card 
                        component={motion.div}
                        whileHover={{ 
                          scale: 1.02,
                          transition: { duration: 0.2 }
                        }}
                        sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                      >
                        <CardMedia
                          component="img"
                          height="160"
                          image={item.image}
                          alt={item.name}
                          sx={{ objectFit: 'cover' }}
                        />
                        <CardContent sx={{ flexGrow: 1, p: 2 }}>
                          <Typography variant="h6" gutterBottom noWrap>
                            {item.name}
                          </Typography>
                          <Tooltip title={item.description}>
                            <Typography 
                              variant="body2" 
                              color="text.secondary" 
                              sx={{
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                whiteSpace: 'nowrap',
                                mb: 2
                              }}
                            >
                              {item.description}
                            </Typography>
                          </Tooltip>
                          <Box sx={{ 
                            display: 'flex', 
                            justifyContent: 'space-between',
                            alignItems: 'center'
                          }}>
                            <Typography variant="h6" color="primary">
                              ₹{item.price}
                            </Typography>
                            <Button 
                              size="small"
                              variant="contained" 
                              color="primary"
                              startIcon={<ShoppingCartIcon />}
                            >
                              Add
                            </Button>
                          </Box>
                        </CardContent>
                      </Card>
                    </motion.div>
                  </Grid>
                ))}
              </Grid>
            </Grid>

            {/* Beverages Section - Made Sticky */}
            <Grid item xs={12} md={3}>
              <Box sx={{ 
                position: 'sticky',
                top: 20,
                maxHeight: 'calc(100vh - 40px)',
                overflowY: 'auto',
                pb: 2
              }}>
                <Paper elevation={3} sx={{ p: 2, borderRadius: 2 }}>
                  <Typography variant="h5" gutterBottom sx={{ mb: 2 }}>
                    Beverages
                  </Typography>
                  <Stack spacing={2}>
                    {beverages.map(beverage => (
                      <motion.div key={beverage.id} variants={itemVariants}>
                        <Card 
                          component={motion.div}
                          whileHover={{ 
                            scale: 1.02,
                            transition: { duration: 0.2 }
                          }}
                        >
                          <Box sx={{ display: 'flex', height: 120 }}>
                            <CardMedia
                              component="img"
                              sx={{ width: 120 }}
                              image={beverage.image}
                              alt={beverage.name}
                            />
                            <CardContent sx={{ 
                              flex: '1 0 auto', 
                              p: 2,
                              display: 'flex',
                              flexDirection: 'column',
                              justifyContent: 'space-between'
                            }}>
                              <Box>
                                <Typography variant="h6" noWrap>
                                  {beverage.name}
                                </Typography>
                                <Typography variant="body2" color="text.secondary" noWrap>
                                  {beverage.description}
                                </Typography>
                              </Box>
                              <Box sx={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                mt: 1
                              }}>
                                <Typography variant="h6" color="primary">
                                  ₹{beverage.price}
                                </Typography>
                                <Button 
                                  size="small"
                                  variant="outlined" 
                                  color="primary"
                                >
                                  Add
                                </Button>
                              </Box>
                            </CardContent>
                          </Box>
                        </Card>
                      </motion.div>
                    ))}
                  </Stack>
                </Paper>
              </Box>
            </Grid>
          </Grid>
        </motion.div>
      </Container>

      <Footer />
    </Box>
  );
};

export default HomePage;
