import React, { useState } from 'react';
import { 
  Box, 
  Container, 
  Grid, 
  Typography, 
  Tabs, 
  Tab,
  Card,
  CardMedia,
  CardContent,
  Button,
  IconButton,
  AppBar,
  Toolbar,
  useTheme,
  useMediaQuery
} from '@mui/material';
import { motion } from 'framer-motion';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import RestaurantMenuIcon from '@mui/icons-material/RestaurantMenu';

// Mock data for food items
const foodItems = [
  { 
    id: 1, 
    name: 'Kerala Biriyani', 
    type: 'non-veg', 
    price: 250, 
    image: 'https://images.unsplash.com/photo-1563379091339-03b21ab4a4f8?w=500&q=80',
    description: 'Aromatic rice cooked with spices and tender meat'
  },
  { 
    id: 2, 
    name: 'Masala Dosa', 
    type: 'veg', 
    price: 120, 
    image: 'https://images.unsplash.com/photo-1589301760014-d929f3979dbc?w=500&q=80',
    description: 'Crispy rice crepe filled with spiced potatoes'
  },
  { 
    id: 3, 
    name: 'Fish Curry', 
    type: 'non-veg', 
    price: 180, 
    image: 'https://images.unsplash.com/photo-1626777552726-4a6b54c97e46?w=500&q=80',
    description: 'Traditional Kerala style fish curry'
  },
  { 
    id: 4, 
    name: 'Vegetable Thali', 
    type: 'veg', 
    price: 150, 
    image: 'https://images.unsplash.com/photo-1546833999-b9f581a1996d?w=500&q=80',
    description: 'Complete meal with variety of vegetables'
  }
];

const beverages = [
  { 
    id: 1, 
    name: 'Kerala Coffee', 
    price: 40, 
    image: 'https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=500&q=80',
    description: 'Traditional filter coffee'
  },
  { 
    id: 2, 
    name: 'Masala Chai', 
    price: 30, 
    image: 'https://images.unsplash.com/photo-1561336313-0bd5e0b27ec8?w=500&q=80',
    description: 'Spiced Indian tea'
  }
];

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { 
    opacity: 1,
    transition: { 
      duration: 0.5,
      when: "beforeChildren",
      staggerChildren: 0.1
    }
  }
};

const itemVariants = {
  hidden: { y: 20, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      type: "spring",
      stiffness: 100
    }
  }
};

const HomePage = () => {
  const [filter, setFilter] = useState('all');
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleFilterChange = (event, newValue) => {
    setFilter(newValue);
  };

  const filteredItems = foodItems.filter(item => 
    filter === 'all' ? true : item.type === filter
  );

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
      <AppBar position="static" color="transparent" elevation={0}>
        <Toolbar>
          <RestaurantMenuIcon sx={{ mr: 2, color: 'primary.main' }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: 'primary.main', fontWeight: 600 }}>
            Thalassery Restaurant
          </Typography>
          <IconButton color="primary" aria-label="cart">
            <ShoppingCartIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 4 }}>
        <motion.div
          initial="hidden"
          animate="visible"
          variants={containerVariants}
        >
          {/* Hero Section */}
          <Box sx={{ 
            textAlign: 'center', 
            mb: 6,
            position: 'relative',
            overflow: 'hidden',
            py: 8,
            borderRadius: 4,
            bgcolor: 'primary.main',
            color: 'white'
          }}>
            <motion.div
              animate={{ 
                scale: [1, 1.02, 1],
                rotate: [0, 1, 0] 
              }}
              transition={{ 
                duration: 5,
                repeat: Infinity,
                repeatType: "reverse"
              }}
            >
              <Typography variant="h1" component="h1" gutterBottom>
              Thalassery Restaurant
              </Typography>
              <Typography variant="h5" sx={{ mb: 4, opacity: 0.9 }}>
                Experience the taste of tradition
              </Typography>
            </motion.div>
          </Box>

          {/* Filter Tabs */}
          <Box sx={{ 
            borderRadius: 2,
            mb: 4,
            bgcolor: 'background.paper',
            boxShadow: '0 2px 12px rgba(0,0,0,0.08)'
          }}>
            <Tabs 
              value={filter} 
              onChange={handleFilterChange}
              centered
              sx={{
                '& .MuiTab-root': {
                  fontSize: '1.1rem',
                  fontWeight: 500,
                  py: 2
                }
              }}
            >
              <Tab label="All Menu" value="all" />
              <Tab label="Vegetarian" value="veg" />
              <Tab label="Non-Vegetarian" value="non-veg" />
            </Tabs>
          </Box>

          <Grid container spacing={4}>
            {/* Menu Items */}
            <Grid item xs={12} md={8}>
              <Grid container spacing={3}>
                {filteredItems.map(item => (
                  <Grid item xs={12} sm={6} key={item.id}>
                    <motion.div variants={itemVariants}>
                      <Card 
                        component={motion.div}
                        whileHover={{ 
                          scale: 1.03,
                          transition: { duration: 0.2 }
                        }}
                      >
                        <CardMedia
                          component="img"
                          height="200"
                          image={item.image}
                          alt={item.name}
                          sx={{ objectFit: 'cover' }}
                        />
                        <CardContent>
                          <Typography variant="h6" gutterBottom>
                            {item.name}
                          </Typography>
                          <Typography variant="body2" color="text.secondary" gutterBottom>
                            {item.description}
                          </Typography>
                          <Box sx={{ 
                            display: 'flex', 
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            mt: 2
                          }}>
                            <Typography variant="h6" color="primary">
                              ₹{item.price}
                            </Typography>
                            <Button 
                              variant="contained" 
                              color="primary"
                              startIcon={<ShoppingCartIcon />}
                            >
                              Add to Cart
                            </Button>
                          </Box>
                        </CardContent>
                      </Card>
                    </motion.div>
                  </Grid>
                ))}
              </Grid>
            </Grid>

            {/* Beverages Section */}
            <Grid item xs={12} md={4}>
              <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
                Beverages
              </Typography>
              <Grid container spacing={2}>
                {beverages.map(beverage => (
                  <Grid item xs={12} key={beverage.id}>
                    <motion.div variants={itemVariants}>
                      <Card 
                        component={motion.div}
                        whileHover={{ 
                          scale: 1.02,
                          transition: { duration: 0.2 }
                        }}
                      >
                        <CardMedia
                          component="img"
                          height="140"
                          image={beverage.image}
                          alt={beverage.name}
                        />
                        <CardContent>
                          <Typography variant="h6" gutterBottom>
                            {beverage.name}
                          </Typography>
                          <Typography variant="body2" color="text.secondary" gutterBottom>
                            {beverage.description}
                          </Typography>
                          <Box sx={{ 
                            display: 'flex', 
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            mt: 2
                          }}>
                            <Typography variant="h6" color="primary">
                              ₹{beverage.price}
                            </Typography>
                            <Button 
                              variant="outlined" 
                              color="primary"
                              startIcon={<ShoppingCartIcon />}
                            >
                              Add
                            </Button>
                          </Box>
                        </CardContent>
                      </Card>
                    </motion.div>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
        </motion.div>
      </Container>
    </Box>
  );
};

export default HomePage;
